CREATE TABLE Scenario (ID BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, PRIMARY KEY (ID))
CREATE TABLE MicroSequence (ID BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, PRIMARY KEY (ID))
CREATE TABLE DialogueSession (ID BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, PRIMARY KEY (ID))
CREATE TABLE Phrase (ID BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, agressiveLevel INTEGER NOT NULL, clearLevel INTEGER NOT NULL, expression VARCHAR(255) NOT NULL, longLevel INTEGER NOT NULL, PRIMARY KEY (ID))
CREATE TABLE Scenario_MicroSequence (Scenario_ID BIGINT NOT NULL, microSequences_ID BIGINT NOT NULL, PRIMARY KEY (Scenario_ID, microSequences_ID))
CREATE TABLE MicroSequence_DialogueSession (MicroSequence_ID BIGINT NOT NULL, dialogueSessions_ID BIGINT NOT NULL, PRIMARY KEY (MicroSequence_ID, dialogueSessions_ID))
ALTER TABLE Scenario_MicroSequence ADD CONSTRAINT ScnrMcrSqmcrSqncsD FOREIGN KEY (microSequences_ID) REFERENCES MicroSequence (ID)
ALTER TABLE Scenario_MicroSequence ADD CONSTRAINT ScnrMcrSqenceScnrD FOREIGN KEY (Scenario_ID) REFERENCES Scenario (ID)
ALTER TABLE MicroSequence_DialogueSession ADD CONSTRAINT McrSqncDlgMcrSqncD FOREIGN KEY (MicroSequence_ID) REFERENCES MicroSequence (ID)
ALTER TABLE MicroSequence_DialogueSession ADD CONSTRAINT McrSqncDldlgSssnsD FOREIGN KEY (dialogueSessions_ID) REFERENCES DialogueSession (ID)
