CREATE TABLE Thing (ID NUMBER(19) NOT NULL, DTYPE VARCHAR(31), name VARCHAR NOT NULL UNIQUE, PRIMARY KEY (ID))
CREATE TABLE DialogueSession (ID NUMBER(19) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE PhraseActor (ID NUMBER(19) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE DoctorActor (ID NUMBER(19) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE MicroSequence (ID NUMBER(19) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE PatientActor (ID NUMBER(19) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE Phrase (ID NUMBER(19) NOT NULL, agressiveLevel NUMBER(10) NOT NULL, clearLevel NUMBER(10) NOT NULL, expression VARCHAR NOT NULL, longLevel NUMBER(10) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE Scenario (ID NUMBER(19) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE MicroSequence_DialogueSession (MicroSequence_ID NUMBER(19) NOT NULL, dialogueSessions_ID NUMBER(19) NOT NULL, PRIMARY KEY (MicroSequence_ID, dialogueSessions_ID))
CREATE TABLE Scenario_MicroSequence (Scenario_ID NUMBER(19) NOT NULL, microSequences_ID NUMBER(19) NOT NULL, PRIMARY KEY (Scenario_ID, microSequences_ID))
ALTER TABLE DialogueSession ADD CONSTRAINT FK_DialogueSession_ID FOREIGN KEY (ID) REFERENCES Thing (ID)
ALTER TABLE PhraseActor ADD CONSTRAINT FK_PhraseActor_ID FOREIGN KEY (ID) REFERENCES Thing (ID)
ALTER TABLE DoctorActor ADD CONSTRAINT FK_DoctorActor_ID FOREIGN KEY (ID) REFERENCES Thing (ID)
ALTER TABLE MicroSequence ADD CONSTRAINT FK_MicroSequence_ID FOREIGN KEY (ID) REFERENCES Thing (ID)
ALTER TABLE PatientActor ADD CONSTRAINT FK_PatientActor_ID FOREIGN KEY (ID) REFERENCES Thing (ID)
ALTER TABLE Phrase ADD CONSTRAINT FK_Phrase_ID FOREIGN KEY (ID) REFERENCES Thing (ID)
ALTER TABLE Scenario ADD CONSTRAINT FK_Scenario_ID FOREIGN KEY (ID) REFERENCES Thing (ID)
ALTER TABLE MicroSequence_DialogueSession ADD CONSTRAINT MicroSequence_DialogueSession_dialogueSessions_ID FOREIGN KEY (dialogueSessions_ID) REFERENCES Thing (ID)
ALTER TABLE MicroSequence_DialogueSession ADD CONSTRAINT FK_MicroSequence_DialogueSession_MicroSequence_ID FOREIGN KEY (MicroSequence_ID) REFERENCES Thing (ID)
ALTER TABLE Scenario_MicroSequence ADD CONSTRAINT FK_Scenario_MicroSequence_microSequences_ID FOREIGN KEY (microSequences_ID) REFERENCES Thing (ID)
ALTER TABLE Scenario_MicroSequence ADD CONSTRAINT FK_Scenario_MicroSequence_Scenario_ID FOREIGN KEY (Scenario_ID) REFERENCES Thing (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT NUMBER(19), PRIMARY KEY (SEQ_NAME))
